// Plento
void mainImage( out vec4 f, in vec2 u ){
    f = texture(iChannel0, u/R);
}

void mainImage( out vec4 f, in vec2 u ){
    vec4 bA = B(u);
    vec2 d = vec2(0);

    if(iMouse.z > 0.){
        vec2 mdx = clamp((C(u).xy - m), -15., 15.);
        d -= mdx * exp(-length(m - u)*.05)*5.;
    }

    vec4 new = B(u - d);
    bA = mix(bA, new, Smear_Factor);

    if(iFrame < 15){bA = og(u);}

    f = bA;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    fragColor = vec4(iMouse.xy, 0.,1.);
}
