#define Rot(a)   mat2(cos(a),sin(a),-sin(a),cos(a))


//   Dave Hoskins hash functions
//   https://www.shadertoy.com/view/4djSRW
vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(443.897, 441.423, 437.195));
    p3 += dot(p3, p3.yzx+19.19);
    return -1.0+2.0*fract((p3.xx+p3.yz)*p3.zy);

}


//  IQ's Gradient Noise 2D
//  https://www.shadertoy.com/view/XdXGW8
float Noise( in vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );

	vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( hash22( i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ),
                     dot( hash22( i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( hash22( i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ),
                     dot( hash22( i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}


float Distance(vec2 uv, float radius, float id)
{
    return length(uv) - radius + 0.12*Noise(2.0*uv+id+2.0);
}


vec2 Cutout(vec2 uv, float radius, float id, float eps)
{
    vec2  shadowTransl = 0.05*vec2(-1.0,1.0);
    float shadowRadius = 0.11;

    float mask   = smoothstep(0.0, eps, Distance(uv, radius, id));
    float shadow = (1.0-0.65*smoothstep(-shadowRadius, shadowRadius, Distance(uv+shadowTransl, radius, id)));

    return vec2(mask, shadow);
}


float Paper(vec2 uv)
{
    uv *= 2.0;

    float   g;
    g  = texture(iChannel0, uv*vec2(1.0,0.01)).r;
    g *= texture(iChannel0, uv*vec2(0.01,1.0)).r;
    g  = 0.7*g +texture(iChannel0, 0.5*uv).r;
    return  g;
}


vec3 Bump(vec2 uv, float eps)
{
    vec2   delta = vec2(eps, 0.0);
    float  a     = Paper(uv);
    float  b     = Paper(uv + delta.xy);
    float  c     = Paper(uv + delta.yx);

    vec3   n     = normalize(vec3(b-a, c-a, 7.5));

    return n;
}


vec3 Shade(vec3 col, vec2 uv, float r, float a, vec3 color, float eps)
{
    // Get mask and shadow:
	vec2 ms = Cutout(uv, 1.07*r, a, eps);

    // Paper bump;
    vec3  normal  = Bump(uv*Rot(a), eps);
    vec3  dif     = color*dot(normal, vec3(1.0));

    // Highlight cutout borders:
    float border  = 0.15*smoothstep(0.005+eps, 0.0, Distance(uv, 1.07*r, a));
    dif          += border*(uv.x-uv.y);

    // Apply shadow:
    col = mix(0.57*vec3(0.04, 0.12, 0.18), col, ms.y);

    // Apply masked texture:
    col = mix(col, dif, ms.x);

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// Normalized coordinates:
	float eps = 2.0/iResolution.y;
	vec2  uv  = eps*(fragCoord - 0.5*iResolution.xy);
    float t   = iTime;

	// Render:
	vec3 col = vec3(mix(0.99, 0.93, texture(iChannel1, 2.0*uv).r));

    col = Shade(col, uv-vec2(-0.033*cos(t+2.3), 0.000           ), 0.50, 4.0, vec3(0.65, 0.86, 0.53), eps);
    col = Shade(col, uv-vec2( 0.000,            0.035*sin(t+2.0)), 0.55, 1.0, vec3(0.86, 0.49, 0.49), eps);
    col = Shade(col, uv-vec2( 0.033*sin(t+1.3),-0.033*cos(t+1.3)), 0.61, 2.0, vec3(0.69, 0.80, 0.89), eps);
    col = Shade(col, uv-vec2(-0.036*cos(t+1.0), 0.000           ), 0.67, 3.0, vec3(0.89, 0.65, 0.43), eps);
    col = Shade(col, uv,                                           0.74, 0.0, vec3(0.8), eps);

    // Grading:
 	col *= smoothstep(2.2, 0.7, length(uv));
    col += smoothstep(-0.3, 1.0, uv.x-uv.y)*vec3(255.,201.,14.)*0.00020;
    col *= step(abs(uv.x), 1.0);

    fragColor = vec4(col,1.0);
}
