void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

        fragColor = vec4(0,0,0,1);       

    float balanco=(sin(iTime+(uv.x*4.0))/20.0);

    // Output to screen    
    if(uv.y<0.05+balanco){} 
    else if(uv.x<0.1+balanco){}
    else if(uv.x>0.9+balanco){}
    else if(uv.y>0.8+balanco){}
    else   if(uv.y<0.3+balanco){
        fragColor = vec4(5,0,0,0)*1.0-balanco*5.0;
    }else if(uv.y>0.55+balanco){
        fragColor = vec4(1,1,1,1)*1.0-balanco*5.0; 
    }else
       fragColor = vec4(0,0,.8,0)*1.0-balanco*5.0;     
}
