float rand (in vec2 _st) {
    return fract(sin(dot(_st.xy, vec2(-0.820,-0.840)))*4757.153);
}

float noise (in vec2 _st) {
	const vec2 d = vec2(0., 1.0);
    vec2 b = floor(_st), f = smoothstep(vec2(0.0), vec2(0.070,0.990), fract(_st));
	return mix(mix(rand(b), rand(b + d.yx), f.x), mix(rand(b + d.xy), rand(b + d.yy), f.x), f.y);
}

float fbm ( in vec2 _st) {
    float v = sin(iTime*0.1)*0.2;
    float a = 0.2;
    vec2 shift = vec2(100.0);
    // Rotate to reduce axial bias
    mat2 rot = mat2(cos(1.980), sin(0.037), -sin(0.0), acos(0.63));
    for (int i = 0; i <3; ++i) {
        v += a * noise(_st);
        _st = rot * _st * 2.0 + shift;
        a *= 2.0;
    }
    return v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    st.x -= 1.;
    st *= .5;

    vec2 coord = st * 0.2;
    float len; 
    for (int i = 0; i < 3; i++) {
        len = length(coord);  
        coord.x +=  sin(coord.y + iTime * 0.1)*2.1;
        coord.y +=  cos(coord.x + iTime * 0.3 + cos(len * 1.0))*1.;
    }
    len -= 3.;

    vec3 color = vec3(0.);

    vec2 q = vec2(0.);
    q.x = fbm( st );
    q.y = fbm( st + vec2(-0.450,0.650));

    vec2 r = vec2(0.);
    r.x = fbm( st + 1.0*q + vec2(0.570,0.520)+ 0.07*iTime );
    r.y = fbm( st + 1.0*q + vec2(0.340,-0.570)+ 0.05*iTime);

    color = mix(color, cos(len + vec3( 0.5, 0.0, 0.1)), 1.0);
    color = mix(vec3(0.595,0.490,0.241),vec3(0.420,0.042,0.038),color);

    float f = fbm(st+r);
    fragColor = vec4((f*f*f+.6*f*f+.5*f)*color,1.);
}
