mat2 rotate (float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

float hash (float n) {
    return fract(sin(n)*1234567.89);
}

float Line (vec2 uv, float angle, float width, float radius) {
    uv *= rotate(angle);
    float w = (uv.y-radius)*width;
    float blur = 0.004;
    return smoothstep(w+blur, w-blur, abs(uv.x));
}

vec4 Layer (vec2 uv, float offset, float radius, float alpha) {
    vec4 col = vec4(1.0, 1.0, 1.0, 0.0);
     for (float i=0.0;i<360.0;i+=20.0) {
        float line = Line(uv, radians(i+offset)+(hash(i+offset)*0.3), 0.04, radius);
        col.a += line;
    }
    col.rgb *= 1.0-alpha;
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.xy;

    vec3 col = texture(iChannel0, uv+0.5).rgb*0.4;
    for (float i=0.0;i<1.0;i+=1.0/4.0) {
        float amt = 0.25;//(sin(iTime*7.0)*0.35)+0.65;
        float offset = (1.0-i)*20.0;
        float radius = (1.0-i)*0.4+amt;
        float alpha = ((1.0-i)*0.7)-0.1;
        vec4 layer = Layer(uv, offset, radius, alpha);
        col = mix(col, layer.rgb, layer.a);
    }
    fragColor = vec4(col,1.0);
}
