float createCircle(vec2 uv, vec2 pos, float radius, float blur) {
    float dist = length(uv - pos);
    float color = smoothstep(radius, radius - blur, dist);
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy / iResolution.xy;

    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    vec3 color = vec3(0.0);

    float mask = createCircle(uv, vec2(0), 0.4, 0.05);

    float mouth;
    mouth -= createCircle(uv, vec2(0), 0.3, 0.01);
    mouth += createCircle(uv, vec2(0, 0.075), 0.285, 0.01);

    mask += mouth;
    mask -= createCircle(uv, vec2(-0.175, 0.15), 0.05, 0.01);
    mask -= createCircle(uv, vec2(0.175, 0.15), 0.05, 0.01);

    color = vec3(1.0, 1.0, 0.0);
    color *= mask;

    // Output to screen
    fragColor = vec4(vec3(color),1.0);
}
