#define PI 3.14159265359

vec3 colorA = vec3(0.149,0.141,0.912);
vec3 colorB = vec3(.40,0.533,0.224);

float plot (vec2 st, float pct){
  return  smoothstep( pct-0.01, pct, st.y) -
          smoothstep( pct, pct+0.01, st.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0.0);
    float pct = abs(sin(iTime));
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float r = smoothstep(0.0,1.0, uv.x);
    float g = sin(uv.x*PI);
    float b = pow(uv.x,0.5);

    // Time varying pixel color
    col = mix(colorA, colorB, pct);
    col = mix(col,vec3(.0,1.0,0.0),plot(uv,r));
    col = mix(col,vec3(0.0,1.0,1.0),plot(uv,g));
    col = mix(col,vec3(1.0,0.0,1.0),plot(uv,b));
    // Output to screen
    fragColor = vec4(col,1.0);
}
