float vesica_piscis(vec2 pos, float r) {
  vec2 leftpos =  vec2(pos.x - 0.5 * r, pos.y);
  vec2 rightpos =  vec2(pos.x + 0.5 * r, pos.y);
  float leftdist = length(leftpos) - r;
  float rightdist = length(rightpos) - r;
  float intersetion = max(leftdist, rightdist);
  return intersetion;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
  float dist = vesica_piscis(uv, 1.0);
  float blur = 2.0 / iResolution.y;
  float inside = smoothstep(-blur, blur, dist);
  fragColor = vec4(inside, inside, inside, 1.0);
}
