// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/min(iResolution.y,iResolution.x);

    vec2 p2 = mod(p *5.*(1.35+sin(iTime/5.)), 2.) -vec2(1.);

    // background color
    vec3 bcol = vec3(1., 0.6 , 0.6);

    // animate
    p *=  vec2(1.+ .275*pow(.015-sin(iTime*3.),15.), .8);
    p2 *=  vec2(1.+ .1275*pow(.002-cos(iTime*3.),15.));

    // shape
    p.y = -0.21 - p.y*1.3 + abs(p.x+cos(2.123*iTime)/4.)-p.x*p.x/2.;
    p2.y = -0.21 - p2.y*1.3 + abs(p2.x)-p2.x*p2.x/2.;
	float r = 1.-length(p/1.2);
	float r2 = 1.44-length(p2);

	// color
    float specr = pow(r, 2.)/10.;
    float specw = pow(r, 10.)+specr;
	vec3 hcol = vec3(r+specr,specw,specw);
	vec3 hcol2 = vec3(r2,0.,0.);

    vec3 col = mix( mix( bcol, hcol2, smoothstep( 0.,0.01, r2-.5) ), hcol,smoothstep( 0.,0.01, r-.5) );

    fragColor = vec4(col,1.0);
}
