float noise(vec3 pos)
{
    return fract(sin(length(dot(pos,vec3(501.,301.,1501.))))*1e5);
}

vec3 rot_x (vec3 vec, float angle)
{
    return vec3(vec.x,vec.y*cos(angle)+vec.z*sin(angle),vec.z*cos(angle)-vec.y*sin(angle));
}

vec3 rot_y (vec3 vec, float angle)
{
    return vec3(vec.x*cos(angle)-vec.z*sin(angle),vec.y,vec.x*sin(angle)+vec.z*cos(angle));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.*fragCoord/iResolution.xy -1.;
    fragColor = vec4(0.,0.,0.,1.);
    vec3 startPos = vec3(iTime*.3,cos(iTime),iTime);
    for(int i=0; i<500; i++)
    {
        vec3 lay = rot_y( rot_x(normalize(vec3(uv,1.)), iMouse.y*.003), iMouse.x*0.003);
        vec3 pos = startPos + lay * float(i)*0.03;
        pos = floor(pos*1.);
        float fog = pow(noise(pos),7.)*0.01;
        fragColor.rgb += vec3(fog,0.,0.);
    }
}
