vec2 CP
    (vec2 n, vec2 p)
{
    vec2 r=vec2(log(dot(n, n))/2., atan(n.y,n.x)) ;
    return
        (
            exp(r. x*p. x-r. y*p. y)*
            vec2
            (
                cos(r. x*p. y+r. y*p. x),
                sin(r. x*p. y+r. y*p. x)
                )

            );
}
vec2 CM (vec2 n1, vec2 n2) 
    {
        return vec2(n1. x*n2. x-n1. y*n2. y, n1. x*n2. y+n1. y*n2. x) ;
       } 

float M(vec2 coo, vec2 R, vec2 p) 
{
    int it=25;

    R=R+coo;
    vec2 dR=vec2(-1.,0.);


    for (;it>0;it--)
    {
        dR=CM(CP(R, p-vec2(1.,0.)),CM(dR,p));
        R=CP(R,p)+coo;
    };
        return (/*0.5*log(dot(R,R))  */ +((256.+sqrt(dot(R, R))) /sqrt((dot(dR,dR))))) /256. ;

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos=vec2(0.,0.);float zoom=4.;
	fragColor=
        vec4(
            M((((fragCoord-iResolution.xy/2.)/iResolution.y)*zoom+pos) , vec2(-0.,0), vec2(iTime,0.)),
            M((((fragCoord-iResolution.xy/2.)/iResolution.y)*zoom+pos),vec2(0.1,0.), vec2 (iTime,-iTime)) ,
            M(vec2(-1.0,  0.1),(((fragCoord-iResolution.xy/2.)/iResolution.y)*zoom+pos),vec2 (iTime,0.1)),
            1.
            );


}
