#define S(a, b, t) smoothstep(a, b, t)
#define PI 3.14159265359
#define TWO_PI 6.28318530718

vec4 makeStar(vec2 uv, vec2 p, float size){
    // Angle and radius from the current pixel
    uv -= p;
    uv = uv/size;
    float a = atan(-uv.x,uv.y)+PI;
    float b = atan(uv.x,uv.y)+PI;
    float r = TWO_PI/float(3);

    // Shaping function that modulate the distance
    float d = cos(floor(.5+a/r)*r-a+.3)*length(uv);
    float e = cos(floor(.5+b/r)*r-b+.3)*length(uv);
    d = min(d,e);
    return vec4(1.-S(.1,.1,d));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 UV = uv;
	//uv.x = abs(uv.x);

    vec4 col = vec4(.94);

	float d = 0.0;
    //uv.x = abs(uv.x);
  	uv *= 10.+2.*sin(iTime*0.7);
    //uv += iTime*0.9;

    //uv += cos(UV*3.14);

    vec2 id=floor(uv);
    float checker = mod(id.y,2.);

    vec4 star0 = makeStar(UV, vec2(0.,-0.1), 1.2); 

    vec2 gv = fract(uv);  // split domains

    vec4 star = makeStar(gv, vec2(0.5,0.5), .7);

    vec4 star1 = makeStar(gv, vec2(0., 0.5), .7);
    vec4 star2 = makeStar(gv, vec2(1.,0.5), .7);


    //col.rg = UV;
    float fade = (1.-abs(UV.x))*(.7-UV.y);
    //col = mix(col,star0-0.1, star0.a);

    if(checker == 1.0){
    	col = mix(col, star, star.a*fade);
    }else{
    	col = mix(col, star1, star1.a*fade);
    	col = mix(col, star2, star2.a*fade);
    }

   	//col.rg += id*.2; // show cells
    //col.rg = gv;  // show domains
    //col.rg *= UV;

    fragColor = col;
}
