float hash( float n )
{
	return fract( sin(n)*500000.);
}

float noise( vec2 p )
{
	return hash( p.x + p.y * 40.*iTime*.2 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	float n = noise(uv);
	fragColor = vec4(vec3(n),1.0);
}
