vec2 N(float angle) {
    return vec2(sin(angle), cos(angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv *= 1.25;
    vec2 mouse = iMouse.xy/iResolution.xy;

    vec3 col = vec3(0);

    float a = 5./6. * 3.1415;

    uv.x = abs(uv.x);
    uv.y += tan(a) * 0.5;

    vec2 n = N(a);
    float d = dot(uv-vec2(0.5, 0.),n);
    uv -= n * max(0., d) *2.;

    //col += smoothstep(0.01, 0., abs(d));

    float scale = 1.;
    n = N(2./3. * 3.1415);
    uv.x += 1.5/3.;

    for (int i=0; i<4; i++) {    
        uv *= 3.;
        scale *= 3.;
        uv.x -= 1.5;

        uv.x = abs(uv.x);
        uv.x -= .5;
        uv -= 2.* n * min(dot(uv, n), 0.);
    }

    d = length(uv - vec2(clamp(uv.x, -1., 1.), 0));
    col += smoothstep(1./iResolution.y, .0,  d / scale);    
    uv/=scale;
    col += texture(iChannel0, uv*0.2 + iTime*0.03).rgb;

    fragColor = vec4(col,1.0);
}
