vec2 circleClose(vec2 uv,vec2 C,float r){
    return C + r*(uv-C)/length(uv-C);
}
float T(float s){
	return sin(iTime*s)/2.+.5;
}
//----------------------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float radius = .2;
    vec2 C = vec2(cos(iTime*2.),sin(iTime*2.))*.2;
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec2 i = circleClose(uv,C,radius);
    float d = distance(uv,i);
    vec3 col = vec3(smoothstep(radius*.1,radius*.1-.01,d));


    fragColor = vec4(col,1.0);
}
