float sdLine( in vec2 p, in vec2 a, in vec2 b )  // tiq
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;

    vec2 start = vec2(0.0, 0.5);

    vec2 finish = vec2(1.0, 0.5);

    float fftwaveform = texture(iChannel0, vec2(uv.x,1.0)).r;

    float tdist = sdLine(vec2(uv.x, uv.y+((fftwaveform)-0.5)*uv.x*(1.0-uv.x)*2.3), start, finish);

    float brightness = 1.10-tdist*45.0;

    vec3 col = vec3(brightness*0.8, brightness*0.8, brightness*30.4);

    fragColor = vec4(col,1.0);
}
