void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    fragColor = texture(iChannel0, uv);
}

#define _Size 0.01
#define _Speed 0.5
#define animTime 10./_Speed
#define Time fract(iTime/(animTime+3./_Speed))*(animTime+1./_Speed)

vec2 lineID(vec2 line, float ID)
{


	return line * (1. - clamp( ID - floor(Time*_Speed),0.,1.))  * max(0.,ID+1.-floor(Time*_Speed));
}

vec2 circular(vec2 center, float radius, float start, float cut)
{
    float PI = 3.141532;
    cut *= Time*2.*PI*_Speed+start*PI*4.;
    return vec2(sin(cut)*radius,cos(cut)*radius) + center ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy/2.)/iResolution.x;    
    fragColor = texture(iChannel0,fragCoord/iResolution.xy);   
    float T = Time*_Speed;
    uv.x += sin(Time*10.)/500.;
        //POINTS
        //A
        vec2 p0 = vec2(-0.4,-0.2);
        vec2 p1 = vec2(-0.3,0.2);
        vec2 p2 = vec2(-0.2,-0.2);

        vec2 p3 = vec2(-0.35,0.);
        vec2 p4 = vec2(-0.25,0.);
        // Dot
        vec2 p5 = vec2(-0.15,-0.2);
        //B
        vec2 p6 = vec2(-0.1,-0.2);
        vec2 p7 = vec2(-0.1,0.2);
        vec2 p8 = vec2(-0.1,0.12);
        vec2 p9 = vec2(-0.1,-0.08);
        // Dot
        vec2 p10 = vec2(0.05,-0.2);
        //C
        vec2 p11 = vec2(0.35,0.);
        // Dot
        vec2 p12 = vec2(0.30,-0.2);
        //END POINTS

    vec2 center =    lineID(mix(p0,p1,fract(T)),							0.)
        			+lineID(mix(p1,p2,fract(T)),							1.)
                	+lineID(mix(p3,p4,fract(T)),							2.)
        			+lineID(circular(p5,0.02,0.0,1.),						3.)
        			+lineID(mix(p6,p7,fract(T)),							4.)
        			+lineID(circular(p8,0.075,0.5,0.5),						5.)
        			+lineID(circular(p9,0.12,0.0,0.5),						6.)
        			+lineID(circular(p10,0.02,0.0,1.),						7.)
        			+lineID(circular(p11,0.2,0.57,0.7)*vec2(0.6,1.),		8.)
        			+lineID(circular(p12,0.02,0.0,1.),						9.);

    float size = _Size;
    float distMask = smoothstep(size*0.8,size,distance(uv, center));
    vec4 initialT = vec4(0.2,0.4,0.0,1.0);
    float erase = Time<animTime ? 0. : 5.7;
    erase *= iTimeDelta;
    fragColor = distMask < size && Time < animTime ? mix(initialT,fragColor,distMask)*iTimeDelta*70.+fragColor : max(vec4(0.),fragColor-erase);

}
