void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 xy = fragCoord.xy / iResolution.xy;

   vec4 texColor = texture(iChannel0,xy);
   vec4 texColor2 = texture(iChannel1,xy);
   vec4 texColor3 = texture(iChannel2,xy);
   vec4 texColor4 = texture(iChannel3,xy);


    if (iTime < 10.0) {
    texColor.r *= abs(sin(iTime));
    texColor.g *= abs(cos(iTime));
    texColor.b *= abs(sin(iTime));
     texColor.xy = abs(sin(xy/iTime-2.0));

        fragColor = texColor;    
    }

     else if ( iTime > 10.0 && iTime < 20.0) {
    texColor2.r *= abs(cos(iTime));
    texColor2.g *= abs(sin(iTime));
    texColor2.b *= abs(cos(iTime));
       texColor2.xy = abs(cos(xy*iTime - 10.0 ));

      fragColor = texColor2;   

    }

    else if ( iTime > 20.0 && iTime < 30.0) {
    texColor3.r *= abs(sin(iTime));
    texColor3.g *= abs(cos(iTime));
    texColor3.b *= abs(sin(iTime));
          texColor3.xy = abs(sin(xy/iTime- 20.0));

      fragColor = texColor3;

    }

     else if ( iTime > 30.0 && iTime < 40.0) {
    texColor4.r *= abs(cos(iTime));
    texColor4.g *= abs(sin(iTime));
    texColor4.b *= abs(cos(iTime));
         texColor4.xy = abs(cos(xy*iTime - 30.0 ));

        fragColor = texColor4;    
    }
    else {
         texColor.b *= abs(cos(iTime));
          fragColor = texColor;    
    }


}
