mat2 Rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(vec2(c, -s),vec2( s, c));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;






    float folha=length(p)+length(p.y)-0.8;

    float line=max(max(p.y/0.2,-p.x/15.8),max(p.x/15.8,-p.y/0.2))-0.05;




    vec3 col=vec3(1.0,0.5,0.1);





  	if(folha < 0.0) col = vec3(0.0,0.5,0.1);

	if(line< 0.0) col = vec3(0.0,0.6,0.2);

	fragColor = vec4(col,1.0);
}
