#define N 512

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float h[N + 1];
    h[0] = 0.0;
    h[N] = 0.0;
    for(int s = N; s > 1; s /= 2)
    {
        for(int i = s/2; i <= N; i += s)
        {
            float r = texture(iChannel0, vec2(uv.x, 0.5)).x;
            h[i] = 0.5*float(s)/float(N) + (h[i-s/2] + h[i+s/2]) / 2.0;
        }
    }

    float b = step(h[int(uv.x * float(N))], uv.y);
    fragColor = vec4(b);
}
