const float BASE_SCALE = 8.;
const float STROKE_WIDTH = 1.;
const float PERIOD = 3.;

const float TAU = 8. * atan(1.);

vec3 f(float x, float y, float t) {
    return vec3(sin(x * y + t) - (sin(x) + sin(y)),
                y * cos(x * y + t) - cos(x), 
                x * cos(x * y + t) - cos(y));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float scale = BASE_SCALE / min(iResolution.x, iResolution.y);
    vec2 uv = (2.0*fragCoord-iResolution.xy) * scale;;

	vec3 f = f(uv.x, uv.y, iTime / PERIOD * TAU);
    float d = abs(f.x) / length(f.yz);

    float col = smoothstep(STROKE_WIDTH+1., STROKE_WIDTH-1., d/2./scale);
    fragColor = vec4(vec3(col), 1.);
}
