const float ROTATION = 2.0 / 360.0 * 2.0 * 3.14159;
const float SCALE = 0.98;

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    if (iFrame < 60 || iMouse.z > 0.0) {
    	fragColor = texture(iChannel1, fragCoord / iResolution.xy);
        return;
    }
    vec4 bg = texture(iChannel0, fragCoord / iResolution.xy);
    vec2 fgCoord = fragCoord / iResolution.xy * vec2(2.0) - vec2(1.0);
    fgCoord = mat2(cos(ROTATION), sin(ROTATION), -sin(ROTATION), cos(ROTATION)) * fgCoord;
    fgCoord *= SCALE;
    fgCoord = (fgCoord + vec2(1.0)) * vec2(0.5);
    vec4 fg = texture(iChannel0, fgCoord);
    fragColor = mix(bg, fg, 0.5);
}
