float circle(vec2 uv, vec2 center, float max, float min)
{
    return smoothstep(max, min, length(uv - center));
}

vec3 tex(vec2 uv)
{
    vec3 color = vec3(1.);// 
    vec3 col = color * circle(uv, vec2(0.), 0.4, 0.39);

    return col;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    vec2 pixel = floor( uv * 200. );

    vec3 col = vec3(0.);
    col += tex( uv * 5. + vec2(0., sin(abs(uv.y) + iTime )) + vec2(cos(iTime*0.213), sin(iTime * 0.213)));
    col += tex( uv * 3. + vec2(0., cos(abs(uv.y) + iTime)) + vec2(cos(iTime*0.113), sin(iTime * 0.113)));
    col += tex( uv * 2. + vec2(0., sin(abs(uv.y + 2.) + iTime)));

    col += tex( uv * 4. + vec2(0., sin(abs(uv.y) + iTime*0.4 )) + 2. * vec2(cos(iTime*0.111), sin(iTime * 0.111)));
    col += tex( uv * 4.3 + vec2(0., sin(abs(uv.y) + iTime*0.04 )) + vec2(cos(iTime*0.513 + 1.1), sin(iTime * 0.213)));
    col += tex( uv * 7. + vec2(0., sin(abs(uv.y) + iTime* 0.008 )) + vec2(cos(iTime*0.313), sin(iTime * 0.313)));


    col = min(col, 1.);

    vec3 c1 = vec3(0.1, 0.8, 0.8);
    vec3 c2 = vec3(0.9, 0.3, 0.3);
    vec3 color = mix(c1, c2, (uv.y+0.5));

    col *= color;

    // Output to screen
    fragColor = vec4(col,1.0);
}
