const int MAX_ITERS = 20;
const float MAX_DIST = 2.0;

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 position = vec2(0.5, 0.0);
    float zoom = 0.333;
    vec2 c = (fragCoord - 0.5 * iResolution.xy) / iResolution.y / zoom - position;
    vec2 z = vec2(0.0, 0.0);
    float iters = 0.0;
    for (int i=0; i < MAX_ITERS; i++) {
        z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) + c;
        if (length(z) > MAX_DIST) {
            break;
        }
        iters++;
    }
    fragColor = vec4(vec3(iters / float(MAX_ITERS)), 1.0);
}
