const float Pi = 3.14159;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 q=7.0*(fragCoord.xy-0.5*iResolution.xy)/max(iResolution.x,iResolution.y);
    vec2 p=vec2(abs(q.x),q.y);

	for(int i=1;i<40;i++)
	{
		vec2 newp=p;
		newp.x+=(0.5/(1.0*float(i)))*cos(float(i)*p.y+iTime*11.0/37.0+0.03*float(i))+1.3;		
		newp.y+=(0.5/(1.0*float(i)))*cos(float(i)*p.x+iTime*17.0/41.0+0.03*float(i+10))+1.9;
		p=newp;
	}

	vec3 col=vec3(0.5*sin(3.0*p.x)+0.5,0.5*sin(3.0*p.y)+0.5,sin(1.3*p.x+1.7*p.y));
    col.x=smoothstep(0.90,1.0,col.x);   
    col.y=smoothstep(0.90,1.0,col.y);
    col.z=smoothstep(0.90,1.0,col.z);
    float f=col.x;
    f=max(f,col.y);
    f=max(f,col.z);
    f=(f*20.)-19.;       

	fragColor=vec4(f,0.75*f,0.1,1.0);

}
