//https://iquilezles.org/articles/distfunctions2d
float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,vec2(0))) + min(max(d.x,d.y),0.0);
}

//https://iquilezles.org/articles/distfunctions2d
float sdTriangle( in vec2 p, in vec2 p0, in vec2 p1, in vec2 p2 )
{
    vec2 e0 = p1-p0, e1 = p2-p1, e2 = p0-p2;
    vec2 v0 = p -p0, v1 = p -p1, v2 = p -p2;
    vec2 pq0 = v0 - e0*clamp( dot(v0,e0)/dot(e0,e0), 0.0, 1.0 );
    vec2 pq1 = v1 - e1*clamp( dot(v1,e1)/dot(e1,e1), 0.0, 1.0 );
    vec2 pq2 = v2 - e2*clamp( dot(v2,e2)/dot(e2,e2), 0.0, 1.0 );
    float s = sign( e0.x*e2.y - e0.y*e2.x );
    vec2 d = min(min(vec2(dot(pq0,pq0), s*(v0.x*e0.y-v0.y*e0.x)),
                     vec2(dot(pq1,pq1), s*(v1.x*e1.y-v1.y*e1.x))),
                     vec2(dot(pq2,pq2), s*(v2.x*e2.y-v2.y*e2.x)));
    return -sqrt(d.x)*sign(d.y);
}

bool reveal = false;

float sdBlock( in vec2 p, in vec2 t,  vec2 b )
{
    vec2 s = b*.5;
    return sdBox(p-s-t, s);
}


void block1(inout vec4 canvas,  vec2 p, vec2 t, vec4 colorBg, vec4 colorFg, float board){
    p -= t;
    float d = 1.;
    d = min(d, sdBlock(p, vec2(0.), vec2(5.,1.)));
    d = min(d, sdBlock(p, vec2(2.,1.), vec2(3.,1.)));
    d = min(d, sdBlock(p, vec2(2.,0.), vec2(3.,2.)));
    canvas = mix(canvas, colorBg, .6*step(0.,-d));
    canvas = mix(canvas, colorFg, 1.- smoothstep(0.,board, abs(d)));
}

void block2(inout vec4 canvas,  vec2 p, vec2 t, vec4 colorBg, vec4 colorFg, float board){
    p -= t;
    float d = 1.;
    d = min(d, sdBlock(p, vec2(0.), vec2(2.,1.)));
    d = min(d, sdBlock(p, vec2(0.,1.), vec2(5.,1.)));
    d = min(d, sdBlock(p, vec2(0.,0.), vec2(2.,2.)));
    canvas = mix(canvas, colorBg, .6*step(0.,-d));
    canvas = mix(canvas, colorFg, 1.- smoothstep(0.,board, abs(d)));
}

void block3(inout vec4 canvas,  vec2 p, vec2 t, vec4 colorBg, vec4 colorFg, float board){
    p -= t;
    float d = sdTriangle(  p,  vec2 (0.),  vec2 (8.,3.),  vec2 (8.,0.) );
    canvas = mix(canvas, colorBg, .6*step(0.,-d));
    canvas = mix(canvas, colorFg, 1.- smoothstep(0.,board, abs(d)));
}

void block4(inout vec4 canvas,  vec2 p, vec2 t, vec4 colorBg, vec4 colorFg, float board){
    p -= t;
    float d = sdTriangle(  p,  vec2 (0.),  vec2 (5.,2.),  vec2 (5.,0.));
    canvas = mix(canvas, colorBg, .6*step(0.,-d));
    canvas = mix(canvas, colorFg, 1.- smoothstep(0.,board, abs(d)));
}

vec2 ani(vec2 a, vec2 b, float t){
    t = t<.5 ? 2.*t*t : -1.+(4.-2.*t)*t;
    vec2 tp = vec2(clamp(t*2.,0.,1.), clamp(t*2.-1.,0.,1.));
	return mix(a, b, a.y>b.y ? tp : tp.yx);
}



uint textHex[] = uint[](0x4d792069u, 0x6e66696eu, 0x69746520u, 0x66696e69u,0x74652073u, 0x70616365u,
                        0x40726d6du, 0x63616c20u, 0x32303139u, 0x2f313020u);
uint st(uint i){
    return textHex[i];
}                                  
vec4 char(vec2 p, uint v, float d){                                      
    vec2 v2 = clamp(p / 16., 0.0, 1./16.) -vec2(0.,1./16.) + vec2(float(v%16u)/16., -float(v/16u)/16.);
    return vec4(texture(iChannel1,v2,-1.));                     
}                                                              

float str( vec2 p,  vec2 tp, float s, float sp, int is, int ie, float board) { 
    float chr = 0.;                                             
    for ( uint i = uint(is); i < uint(ie); i++){
       uint v = uint(((st(i/4u)>>(8u*(3u-(i % 4u)))) % 256u));  
       vec2 translateCh = tp+vec2(float(i-uint(is))*sp, 0.); 
       float fade =  (1.);
       chr = max(chr, char(p/s-translateCh, v, board).x * fade );           
    }  
    return chr;                   
}   

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    reveal = iMouse.z > 0.;
    vec2 uv = fragCoord/iResolution.xy;
    vec2 aspectRatio  = vec2( iResolution.x/iResolution.y,1.);
    vec2 p = (uv-.5)*aspectRatio;
	vec2 pt = (uv-.5)*aspectRatio;

    p *= 14.;
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    p += vec2(7.,3.);

    vec4 canvas = vec4(1.);

    float grid = 1.;

    vec2 pg = p + cos(p*1.+iTime)*.2;
    grid = min (grid, smoothstep(0.,0.001, -.15+abs(mod(pg.x,0.5) ) ));
    grid = min (grid, smoothstep(0.,0.001, -.15+abs(mod(pg.y,0.5) ) ));
    grid = max (grid, 1.-step(0.,sdBlock(p, vec2(0.), vec2(13.08, 5.08) )));
    canvas = mix(canvas, vec4(uv+.8,1.,0.), 1.-grid );
    canvas = clamp(canvas, 0., 1.);

    canvas = mix(canvas, vec4(0.), 1.- smoothstep(0.,.1, -.05+ abs(sdBlock(p, vec2(0.), vec2(13.,5.)))));

    grid = min (grid, smoothstep(0.,0.1, abs(mod(p.x,1.) ) ));
    grid = min (grid, smoothstep(0.,0.1, abs(mod(p.y,1.) ) ));
    grid = max (grid, step(0.,sdBlock(p, vec2(0.), vec2(13.08, 5.08) )));
    canvas = mix(canvas, vec4(0.,0.,0., 0.), 1.-grid );

 	canvas = mix(canvas, vec4(0.8),  clamp(str( pt, vec2(-5.,3.), 0.11, .5,  0, 24, 2.),.0,1.));
 	canvas = mix(canvas, vec4(0.,uv.x*.6,.4, 0.),  clamp(str( pt, vec2(-5.1,3.1), 0.11, .5,  0, 24, 0.2),.0,1.));
  	canvas = mix(canvas, vec4(0.,0.,0., 0.),  clamp(str( pt, vec2(12.,7.), 0.04, .5, 24, 39, -1.),.0,1.));


    if (reveal)
    {
        for (float ti = 0.; ti < 2.; ti++)
        {
            block1(canvas,  p, ani(vec2(8.,0.), vec2(8.,0.), ti), vec4(1.-ti,ti, 0., 0.), vec4(0.,0.,0., 0.),0.05);
            block2(canvas,  p, ani(vec2(5.,0.), vec2(8.,1.), ti), vec4(1.-ti,ti, 0., 0.), vec4(0.,0.,0., 0.),0.05);
            block3(canvas,  p, ani(vec2(5.,2.), vec2(0.,0.), ti), vec4(1.-ti,ti, 0., 0.), vec4(0.,0.,0., 0.),0.05);
            block4(canvas,  p, ani(vec2(0.,0.), vec2(8.,3.), ti), vec4(1.-ti,ti, 0., 0.), vec4(0.,0.,0., 0.),0.05);

        }
    }
    else
    {
        float t = abs(fract(iTime*.1)*2.-1.);
        t = t<.5 ? 2.*t*t : -1.+(4.-2.*t)*t;

        float tr = t*4.;
        float t1 = clamp(tr,0., 1.);
        float t2 = clamp(tr-1.,0., 1.);
        float t3 = clamp(tr-2.,0., 1.);
        float t4 = clamp(tr-3.,0., 1.);

        block1(canvas,  p, ani(vec2(8.,0.), vec2(8.,0.), t1), vec4(1.,0.,0., 0.),  vec4(0.,0.,0., 0.),0.1);
        block2(canvas,  p, ani(vec2(5.,0.), vec2(8.,1.), t2), vec4(0.,1.,1., 0.),  vec4(0.,0.,0., 0.),0.1);
        block3(canvas,  p, ani(vec2(5.,2.), vec2(0.,0.), t3), vec4(1.,1.,0., 0.),  vec4(0.,0.,0., 0.),0.1);
        block4(canvas,  p, ani(vec2(0.,0.), vec2(8.,3.), t4), vec4(0.,1.,0., 0.),  vec4(0.,0.,0., 0.),0.1);
    }

    // Output to screen
    fragColor = vec4(canvas.rgb,1.0);
}
