#define PI 3.141592
#define PI_HALF PI * .5
#define TAU PI * 2.

float sdSphere(vec3 p, float r) {
  return length(p) - r;
}

float sdCircle(vec2 p, float r) {
  return length(p) - r;
}

vec4 trans = vec4(0.0, 0.0, 0.0, 0.0);
vec4 color = vec4(1.0, 0.0, 0.0, 1.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ratio = iResolution.x / iResolution.y;
    vec2 correct = vec2(ratio, 1.);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 screen = (fragCoord / iResolution.xy);
    // Normalized device coordinates.
    vec2 ndc = (screen - .5) * 2. * correct;

    float angle = atan(abs(ndc.y), abs(ndc.x));
    vec4 s = texture(iChannel0, vec2(angle / PI, 0.));

    float dist = sdCircle(ndc, s.x);
    float idist = 1. - dist;
    if (dist > 1.0) {
        fragColor = trans;
    } else {
		fragColor = vec4(
            idist,
            idist,
            idist, 
            1.
        );
    }

    float sun = sdCircle(ndc, 0.1);
    float isun = 1.0 - sun;
    fragColor += vec4(isun, isun * 0.5, isun * 0.25, 1.0);       
}
