float divScotFlag( vec2 uv) {
    float botRight = step( uv.x + uv.y, 1. );
    float botLeft = step( uv.x + 1. - uv.y, 1. );

    // Scottish flag ( sorta )
    float scotFlag = abs(botRight - botLeft);
    float divider = step( uv.x, .5);

    // Divided scottish flag
    return abs( divider - scotFlag);
}

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord-.5*iResolution.xy) / iResolution.y; // Aspect Ratio adjustment

     if (iMouse.z <= 0.){

    if (uv.x < -0.5) uv=(uv+0.6)*rotate2d(iTime/5.); //rotate         
    else if (uv.x >= -.5 && uv.x < 0.)  uv.x+=sin(iTime/5.+uv.y);  //wave        
    else if (uv.x >=0. && uv.x < 0.5)  uv*=(2./1.+sin(iTime/2.)); //zoom   
    else if ( uv.x >= 0.5)    uv.y/=((uv.x))+cos(iTime/2.)+0.8; // flower
         }

    else uv=(pow(abs(rotate2d(3.14*.25*sin(iTime/5.))*uv),iMouse.xy/iResolution.y)-iTime/20.);

    uv.y *= 5./3.; // adjust height width ratio

    float d =divScotFlag( fract(uv * 4.) )+.4;

    //rough attempt at adding AA
    float wd = fwidth(d);//float wd = d * 3./iResolution.y; 
    float pattern = smoothstep(1. + wd, 1. - wd, d);

	fragColor = vec4(vec3(pattern), 1.0);
}
