#define RING_COUNT	 5.0
#define RING_SPACING 0.05
#define RING_DENSITY 8.0

#define PI 3.141593

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 center = vec2(0.5);
    vec2 uv = fragCoord / iResolution.xy;

    uv.y *= iResolution.y / iResolution.x;
    center.y *= iResolution.y / iResolution.x;

    float result = 0.0;

    for (float i = RING_COUNT - 1.0; i >= 0.0; --i)
    {
        float rad = RING_SPACING * i;
        float cnt = max(RING_DENSITY * i, 1.0);

        float inc = 2.0 * PI / cnt;
        float off = 0.1 * i + cos(iTime);

        for (float j = 0.0; j < cnt; ++j)
        {
            float theta = off + j * inc;
            vec2 pos = center + rad * vec2(cos(theta), sin(theta));
            result += pow(max(1.0 - distance(uv, pos), 0.0), 5e2);
        }
    }

    fragColor = vec4(vec3(result), 1.0);
}
