float wavy = 0.4;
float xAmp = 0.12;
float xFreq = 0.8;
float yAmp = 0.17;
float yFreq = 0.69;

void mainImage(out vec4 fragColor,in vec2 fragCoord)
	{
    // sample
    vec2 uv = fragCoord/iResolution.xy;

    // output
    if (iMouse.x < fragCoord.x)
    	{
        uv.x += cos(uv.y + iTime*xFreq)*xAmp*wavy;
    	uv.y += sin(uv.x + iTime*yFreq)*yAmp*wavy;
        fragColor = texture(iChannel0,uv);
        }
    else
    	fragColor = texture(iChannel0,uv);
	}
