// variant of https://shadertoy.com/view/tdKSRw

#define C(U,c) texture( iChannel0, fract( (.5*U+.5 + vec2( c, 15-(c)/16 ) ) / 16. ) ).w - .5//
#define rot(a) mat2(cos( 1.57*fract(a) + vec4(0,33,11,0)))              //
void mainImage(out vec4 O, vec2 U) {
    float t = iTime,  d; int c = int(t);
    vec3 R = iResolution, P = vec3(U+U-R.xy,-R.y) / R.y, D = vec3(0,0,1), Z;
 /* if ( abs(P.x) < 1. ) */
      O.a = min(0.,t);  /* anti-unrolling test */
      for ( P.xz *= rot(t), D.xz *= rot(t);
            O.a++ < 99. &&  dot(P,P) < 2. ; )
          Z = vec3( C(P.xy, 65+ c   %26 ),
                    C(P.zy, 65+(c+1)%26 ), 0),
          d = max(Z.x,Z.y), /*  d = length( Z ), */
          d > .005 ? P += d*D, t
                   : O[ d == Z[c%2] ? 1 : 0 ]++;
}
