void mainImage( out vec4 O, vec2 u )
{
    vec2 res = iResolution.xy,
          uv = ( u* 2. - res) / min(res.x, res.y);

    float t = iTime;
    float scale = 2.;
    uv *= scale;

    float p = length(cmul(cmul(uv, uv),uv) + tan(t)* cmul(uv,uv) + sin(t)* uv+ cos(t));

    O = vec4(vec3(0.1 / p), 1.);
}

vec2 cmul(in vec2 a, in vec2 b) {
	return vec2(a.x * b.x - a.y * b.y, a.y * b.x + a.x * b.y);
}
