// Unity / HLSL Compatible Defines ////////////////////////////////////////
#define saturate(a) clamp( a, 0.0, 1.0)
#define frac fract
#define lerp mix
#define float4 vec4
#define float3 vec3
#define float2 vec2
///////////////////////////////////////////////////////////////////////////

#define PixelAA 40.0
#define LineThickness 0.15

float DX1 (float x){return clamp(x,0.0,1.0)+clamp(x-2.0,0.0,1.0);}
float DY1 (float x){return clamp(x-1.0,0.0,1.0)-clamp(x-3.0,0.0,1.0);}

float DX2 (float x){return DY1(x+1.0)+DX1(x-4.0);}
float DY2 (float x){return DX1(x-1.0)+DY1(x-4.0);}

float DX3 (float x){return DY2(8.0-x)+DX2(x-8.0);}
float DY3 (float x){return 2.0-DX2(8.0-x)+DY2(x-8.0);}

float DX4 (float x){return DY3(16.0-x)+DX3(x-16.0);}
float DY4 (float x){return 3.0-DX3(16.0-x)+DY3(x-16.0);}

float DX5 (float x){return DY4(32.0-x)+DX4(x-32.0)-1.0;}
float DY5 (float x){return 6.0-DX4(32.0-x)+DY4(x-32.0);}

float DX6 (float x){return DY5(64.0-x)+DX5(x-64.0)-5.0;}
float DY6 (float x){return 10.0-DX5(64.0-x)+DY5(x-64.0);}

float DX7 (float x){return DY6(128.0-x)+DX6(x-128.0)-10.0;}
float DY7 (float x){return 10.0-DX6(128.0-x)+DY6(x-128.0);}

float DX8 (float x){return DY7(256.0-x)+DX7(x-256.0)-5.0;}
float DY8 (float x){return 5.0-DX7(256.0-x)+DY7(x-256.0);}

float DrawLineAA( float2 p, float2 a, float2 b, float radius){
    float2 V0			= b - a, V1 = p - a;
    float Inter 		= saturate(dot( V0, V1) / dot(V0, V0));
    float Distance 		= length(V1 - V0 * Inter) - radius;
    float AntiAlias 	= saturate(-(Distance * ( iResolution.y / PixelAA)));
    return AntiAlias;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 Buv = vec2(uv.x* iResolution.x / iResolution.y,uv.y);

    float col = 0.0;
    float V = floor(iTime*60.0f);
    float VB = V-1.0;
    float VK = V+1.0;
    //float DistNeg = DrawLineAA(Buv*40.0,vec2(DX8(VB)+1.0,DY8(VB)+1.0),vec2(DX8(V)+1.0,DY8(V)+1.0),LineThickness);
    float Dist = DrawLineAA(Buv*40.0,vec2(DX8(V)+1.0,DY8(V)+1.0),vec2(DX8(VK)+1.0,DY8(VK)+1.0),LineThickness);
    //col = max(DistNeg,max(Dist,texture(iChannel0,uv).x));
    col = max(Dist,texture(iChannel0,uv).x);

    fragColor = vec4(col,col,col,1.00);
}
