Main {
    if (iFrame%N<N-1) discard;
    vec4 m = 0.25*(
        A(U+o.xy)+
        A(U+o.yx)+
        A(U+o.zy)+
        A(U+o.yz)
    );
    Q = m*vec4(1,1.1,1.5,1);;
    Q -= .2*D(U).wwww;
}

vec4 T(vec2 U) {
	U -= .25*C(U).zw;
	U -= .25*C(U).zw;
    return D(U);
}
Main {
    Q.xy = vec2(0);
    vec2 k = 2.*pi*(U-0.5*R)/R;
	for (float i = 0.; i < R.x; i++) {
        vec2 u = vec2(i,U.y);
        vec2 v = vec2(1,0)*ei(iTime*DEPTH*B(u).z);
        Q.xy += v*ei(-k.x*i)/sqrt(R.x);
    }
    Q.zw  = T(U).zw;
    vec4 
        n = B(U+o.yx),
        e = B(U+o.xy),
        s = B(U+o.yz),
        w = B(U+o.zy);
    Q.zw -= 0.25*vec2(e.z-w.z,n.z-s.z);

    if (U.x<1.||R.x-U.x<1.||U.y<1.||R.y-U.y<1.)
        Q.zw *= 0.;
	if (iFrame < 1) {
        Q = vec4(0);
    }
    vec2 mo = 0.5*R;
    if (iMouse.z>0.) mo = iMouse.xy;
    Q.zw = mix(Q.zw,
        .8*vec2(cos(.2*iTime),0)*ei(.05*iTime),
            exp(-.05*dot(U-mo,U-mo)));
}

vec4 T(vec2 U) {
	U -= .25*C(U).zw;
	U -= .25*C(U).zw;
    return D(U);
}
Main {
    Q = vec4(0);
	vec2 k = 2.*pi*(U-0.5*R)/R;
	for (float i = 0.; i < R.y; i++) 
        Q.xy += A(vec2(U.x,i)).xy*ei(-k.y*i)/sqrt(R.y);
    vec2 q = Q.xy;
    Q = vec4(0);
    float l = WAVELENGTH*WAVELENGTH-dot(k,k);
    if (l>0.)
        Q.xy += q * ei(sqrt(l)*HEIGHT);
    else Q.xy += q * exp(-sqrt(abs(l*HEIGHT)));
    Q.zw  = T(U).zw;
    vec4 
        n = B(U+o.yx),
        e = B(U+o.xy),
        s = B(U+o.yz),
        w = B(U+o.zy);
    Q.z = Q.z - 0.25*(e.z-w.z+n.w-s.w);
    vec2 mo = 0.5*R;
    if (iMouse.z>0.) mo = iMouse.xy;
    Q.w = mix(Q.w,1.,
            exp(-.05*dot(U-mo,U-mo)));
}

Main {
    Q = vec4(0);
    vec2 w = 2.*pi*(U-0.5)/R;
    for (float i = 0.; i < R.x; i++) 
        Q.xy += A(vec2(i,U.y)).xy*ei(w.x*i)/sqrt(R.x);
}

Main {
    Q = vec4(0);
    vec2 w = 2.*pi*(U-0.5)/R;
    for (float i = 0.; i < R.y; i++) 
        Q.xy += A(vec2(U.x,i)).xy*ei(w.y*i)/sqrt(R.y);
    Q *= mod(U.x+U.y,2.)*2.-1.;
    vec4 col = exp(-abs(time-vec4(-1.,0,1.,1)))*length(Q);
    Q = B(U);
    if (iFrame%N==0) Q = vec4(0);
    Q += max(col,0.)/float(N);
}
