// adapted from FabriceNeyret2's https://www.shadertoy.com/view/wllcRN
// inspired from python273 https://shadertoy.com/view/4ldyW8
// See also https://www.shadertoy.com/view/ttscRN

float xor_thing(vec2 U){
    vec2 R = iResolution.xy;
    ivec2 I = ivec2(U+3.*iTime);
    int x = I.x, y = I.y,
        i = x - y ^ y + x;

    float v = iTime * 0.75 + float(i);  // try also: i*i , i*i*i

    return (fract( v * 0.051618 ));
}

void mainImage( out vec4 O, vec2 U )
{
    float scale = 1.0;
    float result = 0.0;
    for(int i = 0; i < 10; i++){
        result += xor_thing(U/scale/pow(2.0,10.0))/10.0;
        scale /= 2.0;
    }
    O = vec4(result);
}
