void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //vec2 uv = vec2(fragCoord.x / iResolution.x, 8.0 * fragCoord.y / iResolution.x);
    vec2 uv = fragCoord/iResolution.xy;

    if (fragCoord.y - 2.0 * floor(fragCoord.y / 2.0) > 0.5) {
    	float offset = max(0.0,
            2.0 * pow(uv.x, 2.0) * texture(iChannel1, 0.005 * uv.xy).y +
            - 1.5 * uv.x * texture(iChannel1, 0.001 * uv.xy).y);
        offset = mix(offset, offset - offset * 0.05, pow(fract(uv.x * 50.0), 5.0));
        offset += 0.2 * texture(iChannel1, vec2(3.0 * uv.x, 0.5 * uv.y)).x;
        offset += max(5.0, 20.0 * pow(1.0 * texture(iChannel1, vec2(2.5 * uv.x, 0.3 * uv.y)).x, 5.0)) - 5.0;
        offset += max(5.0, 20.0 * pow(1.0 * texture(iChannel1, vec2(2.5 * uv.x, 0.1 * uv.y)).x, 5.0)) - 5.0;
        if (offset < 0.3) offset = 0.0;
    	uv = vec2(uv.x - 0.2 * pow(uv.x, 5.0) * offset, uv.y);
    }
   	vec4 color = texture(iChannel0, uv);
    fragColor = color;
}

