mat2 rotM(float a) { float c = cos(a), s = sin(a); return mat2(c, -s, s, c); }

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float noise(vec2 p){
	vec2 ip = floor(p);
	vec2 u = fract(p);
	u = u*u*(3.0-2.0*u);

	float res = mix(
		mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
		mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
	return res*res;
}

vec3 randColor(vec2 co) {
	float r = noise(co*vec2(97.777, 123.7973));
    float g = noise(co * vec2(r, 89.7897) + vec2(11.8, 97.6767));
    float b = noise(vec2(r, g)*vec2(11.1317, 77.3443));
    return vec3(r, g, b);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    float fft = texture(iChannel0, vec2(-abs(uv.x), uv.y)).x;
    uv *= .6 * (sin(fft)+1.);

    uv = abs(uv);
    uv.y -= 0.2;
    uv.x -= 0.2;

    float a = (3./8.)*3.1415;
    vec2 n = vec2(cos(a), sin(a));

    uv.y -= .25;
    float scale = 1.;
    for(int i = 0; i < 8; i++) {
        uv.y += .25;
        uv *= 2.;
        scale *= 2.;
        uv.y = -uv.y;
        uv *= rotM(.1*iTime);

        uv.x = abs(uv.x);
        uv -= 2.*n*max(0., dot(uv,n));
    }

	float d = length(uv - vec2(0., clamp(uv.y, -.5, .5)));
    vec3 col = vec3(smoothstep(1./iResolution.y, 0., d/scale));
    col += randColor(.2*uv/scale + .1*iTime + .1*fft).rgb;
    fragColor = vec4(col,1.0);
}
