void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    if(abs(fragCoord.x-iMouse.x)<3.){
        fragColor = vec4(0.,0.,0.,1.);
    }else{
        vec2 uv = fragCoord/iResolution.xy;
        if(fragCoord.x>iMouse.x){
            vec3 col = vec3(0.);
            for(int i=-10; i<11; i++){
                col += texture(iChannel1, uv+vec2(0.,float(i)/iResolution.x)).rgb/(float(i)*float(i)+7.);
            }
            col = texture(iChannel0,uv).rgb+2.*(texture(iChannel0,uv).rgb-col);
            col = col*1.2 - .04;
            fragColor = vec4(col,1.0);
        }else{
            fragColor = vec4(texture(iChannel0,uv));
        }
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(0.);
    for(int i=-10; i<11; i++){
    	col += texture(iChannel0, uv+vec2(float(i)/iResolution.x,0.)).rgb/(float(i)*float(i)+7.);
    }

    fragColor = vec4(col,1.0);
}
