float key(vec2 uv)
{
    vec4 c = texture(iChannel0, uv);

    if(c.g > c.r && c.g > c.b && length(c.rgb) > 0.5)
        return 0.0;
    return 1.0;
}

#define N 7.0

void mainImage( out vec4 fragColor, in vec2 p )
{
    vec2 uv = p/iResolution.xy;

    float c = key(uv);

    vec2 R = p + vec2(+1.0, 0.0);
    vec2 L = p + vec2(-1.0, 0.0);
    vec2 U = p + vec2(0.0, +1.0);
    vec2 D = p + vec2(0.0, -1.0);

    float H = key(L/iResolution.xy)
           - key(R/iResolution.xy);
    float V = key(U/iResolution.xy)
           - key(D/iResolution.xy);

    float edge = length(vec2(H, V));
    //fragColor = vec4(1.0) * (1.0 - edge);

    vec4 color = texture(iChannel0, uv);
    fragColor = mix(vec4(1.0), key(uv) * floor(color * N) / N, c);
    fragColor *= 1.0 - edge; // black outlines

    //fragColor = key(uv) * floor(color * N) / N + edge; // white outlines on black background
}
