#define Tau 0.666

vec3 point(vec2 p) {
	return length(p)<0.005 ? vec3(1.0) : vec3(0.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;  
    vec2 p1 = vec2(0.5+0.5,0.5+1.0);
    vec2 p2 = vec2(0.0,0.5+sin(iTime * 0.4)*0.5);

    float a = p2.y-p1.y;
    float b = p2.x;
    float c = p2.y;

    float x = uv.x;
    float y = -(a*x+c)/b;
    float w = -(a*uv.y+c)/b;
    vec3 col;
    if (uv.y > y) {
    	col = texture(iChannel0, uv).rgb;
    } else {
        float x0 = uv.x;
        float y0 = uv.y;
        vec2 uv2 = vec2(b*( b*x0-a*y0) - a*c,a*(-b*x0+a*y0) - b*c);
        uv2 /= a*a+b*b;
        float r =  texture(iChannel0, uv ).r * Tau;
        float g =  texture(iChannel0, uv2).b;
        float b =  texture(iChannel0, uv2 ).g;
        col = vec3(r,g,b);
    }

    fragColor = vec4(col,1.0);

}
