renderpass.code:
float q(float f, float s) {
    return floor(f * s) / s;
}

float dial(vec2 p, float or, float ir, float t) {
    float dc = 1. - max(length(p) - or, -(length(p) - ir));
    float dr = ((atan(p.x, -p.y) + 3.14159265) / 6.28318530) + fract(t);

    float pw = 2. / iResolution.x;
    return smoothstep(1. - pw, 1., dc) * smoothstep(1. - pw, 1., dr);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.y;
    vec2 p = uv - .5;
    float t = iDate.w;

    float h = dial(p, .5, .4, q(t / 43200., 12.));
    float m = dial(p, .35, .3, q(t / 3600., 60.));
    float s = dial(p, .25, .225, q(t / 60., 60.));
    fragColor = vec4(h + m + s);
}
