void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 sx = vec2(1.0, 0.0) / iResolution.xy;

    vec4 tex1 = texture( iChannel1, uv - sx);
    vec4 tex2 = texture( iChannel1, uv);
    vec4 tex3 = texture( iChannel1, uv + sx);

    vec4 texc = texture( iChannel0, uv);

    float val = (tex1.r > 0.0 || tex2.r > 0.0 || tex3.r > 0.0) && texc.r < 0.5 ? 1.0 : 0.0;

    fragColor = vec4(val, val, val, val);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 tex = texture( iChannel0, uv );

    float val = tex.r > 0.5 || tex.g > 0.5 || tex.b > 0.5 ? 1.0 : 0.0;

    fragColor = vec4(val, val, val, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 sy = vec2(0.0, 1.0) / iResolution.xy;

    vec4 tex1 = texture( iChannel0, uv - sy);
    vec4 tex2 = texture( iChannel0, uv );
    vec4 tex3 = texture( iChannel0, uv + sy);

    float val = tex1.r > 0.0 || tex2.r > 0.0 || tex3.r > 0.0 ? 1.0 : 0.0;

    fragColor = vec4(val, val, val, 1.0);
}
