#define PI 3.14159265359
#define PHI 1.618


vec3 circlesRepeating(vec2 coord, float radius, vec3 color)
{
    vec3 returnVal = vec3(0.);
    vec2 center = vec2(sin(iTime),cos(iTime));

    float circle = .0;

    center.x *= PI * 0.1;
    center.y *= PI * 0.1;
    radius *= cos(abs(coord.x) + abs(coord.y));

    center.x *= fract(coord.x * 600.);
    center.y *= fract(coord.y * 600.);

    center *= PHI;

	circle = pow(coord.x - center.x, 2.) + pow(coord.y - center.y, 2.);
	circle = smoothstep(circle*.9, circle*1.1, pow(radius, 2.));

    return vec3(circle) * color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = fragCoord.xy/iResolution.xy;
    uv -= 0.5;

    vec3 color = circlesRepeating(uv, 0.044, vec3(1.));

    fragColor = vec4(color,1.0);
}
