#define mainImage(f,g)              \
    f = iResolution.yyyx;           \
    vec2 p = g + g - f.wz;          \
    p=vec2( atan( p.x, p.y ),       \
            log( f = length(p)/f )  \
          ) * 3.1831;               \
    p.y += ceil(p.x)/4. - iTime;    \
    p = mod( p+p, 2. );             \
    f = .7*abs( f - length(--p*p) )

