void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 
    float len=1000.0;

    bool isInCircle=false;
    for(float i=0.0;i<5.0;++i){
        float t = i *0.15+0.2;
        vec2 centerPos = t* iResolution.xy;
        float x= fragCoord.x - centerPos.x;
        float y= fragCoord.y - centerPos.y;
        if(x*x + y*y <=len){
          fragColor = vec4(sin(iTime*t),cos(iTime*t),1,0);
          isInCircle=true;
          break;
        }
    }

    if(!isInCircle){
       fragColor=vec4(1,1,1,0);
    }


}
