float Circle(vec2 uv, float r, float blur){
	float d = length(uv);
    float c = smoothstep(r, r + blur, d);
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.y *= iResolution.y/iResolution.x;

    vec2 muv = uv;

    // uv = fract(uv*4.) - .5;
    // float swing =  sin(iTime + length(uv)*59.)*.5 + .5;

    float swing =  sin(iTime)*.5 + .5;
    uv.y += mix(-.1, -.15, swing);

    // uv.x += sin(iTime*1.9)*.1;

    float outerMainCircle = Circle(uv*3., mix(.8, .6 , swing), -0.8);    
    float innerMainCircle = Circle(uv*9., mix(.8, .6 , swing), -0.8);

    float body = outerMainCircle - innerMainCircle;

    vec2 rightEyePos = vec2(uv.x*mix(6., 8., swing) + 0.95, uv.y*8.);
    float rightEye = Circle(rightEyePos +.4, 1.2, -.7) - Circle(rightEyePos, 1.2, -1.2) ;

    vec2 leftEyePos = vec2(uv.x*mix(6., 8., swing) -0.95, uv.y*8.);
    float leftEye = Circle(leftEyePos -.4, 1., -0.5) - Circle(leftEyePos, 1.2, -1.5);

    float fish = body + rightEye + leftEye;

    muv += fish;

    vec4 res = texture(iChannel0, muv+.09);

    // Output to screen
    // Uncomment/comment one of next two lines for two variants.

    // "Fish in darkness"
     fragColor = vec4(vec3(fish, fish, fish-.1),1.0);

    // "Breathing of leaves"
	// fragColor = res;
}
