// CIRCLES

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 coord = fragCoord.xy / iResolution.xy;
    vec3 color = vec3(0.0);
    vec2 translate = vec2(-0.5);
    coord += translate;

    color.r += abs(0.1 + length(coord) - 0.6 * abs(sin(iTime * 0.9) /.95));
    color.g += abs(0.1 + length(coord) - 0.6 * abs(sin(iTime * 0.6) * .85));
    color.b += abs(0.1 + length(coord) - 0.6 * abs(sin(iTime * 0.8) * .49));


    fragColor = vec4(0.05 / color, 1.0);
}
