mat2 rot(float a){
	return mat2(cos(a),-sin(a),sin(a),cos(a));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    //vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.x * 40.;
    float columns = 30.;
    float rows = 10.;
    float interval = 0.03;
    float rowInterval = columns * interval;
    vec2 uv = fragCoord/iResolution.xy;

    uv.x *= columns;
    uv.y *= rows;
    float column = floor(abs(mod(iTime/interval, columns * 2.) - columns)) + 1.;
    float row = floor(abs(mod(iTime/rowInterval, rows * 2.) - rows)) + 1.;
    //slot -= floor(slot);
	if(uv.x < column && uv.x > column-1.){
        if( uv.y < row && uv.y > row-1.){
        	fragColor = vec4(0.0, 1.0, 0.0, 1.0);  
        }
    }
    if(pow(uv.x - round(uv.x), 2.) < 0.01 || pow(uv.y - round(uv.y), 2.) < 0.01){
    	fragColor = vec4(abs(sin(iTime)) + 0.2, abs(sin(iTime*7.3)) + 0.2, abs(sin(iTime*4.)) + 0.2, 1.);
	}
}
