const float width = 0.05; // Width ratio of rectangle
const float height = 0.05; // Height ratio of rectangle

vec2 func1(vec2 uv, float time) {
	float hypot = length(uv);
    float uvang = atan(uv.y, uv.x);
    float newang = uvang + time;
	return hypot * vec2(cos(newang), sin(newang));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){

    fragColor = vec4(0.25, 0.25, 0.25, 1.0);

    vec2 R = iResolution.xy;
    vec2 uv = (fragCoord.xy -.5 * R) / R.y;
    uv.x += cos(iTime) * 0.1;

    vec2 rot = func1(uv, iTime);

    rot = abs( rot ) - vec2( width, height );
    if(rot.x <= width && rot.x >= -width && rot.y <= height && rot.y >= -height){
        fragColor = vec4(1.0, 1.0, 1.0, 1.0);
	}
}
