void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 

    vec4 v = texture(iChannel0,fragCoord / vec2(640.0, 380.0));

    float mask = step( .15, (v.g - v.r));

    vec3 background = fragCoord.xyx / 720.0;

    vec3 c = mix(v.rgb, background, mask);


	fragColor = vec4(c, 1.);
}
